import React, { useState } from "react";
import "./App.css";
import { loveLanguageQuiz } from "./data";

function App() {
  const [index, setIndex] = useState(0);
  const [ans, setAns] = useState({});
  const [result, setResult] = useState("");
  const [loading, setLoading] = useState(false); 

  function handleSelection(optionType) {
    setAns((prev) => ({
      ...prev,
      [index]: optionType,
    }));
  }

  function handleClick() {
    if (!ans[index]) {
      alert("Please select an answer before proceeding!");
      return;
    }

    if (index < loveLanguageQuiz.length - 1) {
      setIndex(index + 1);
    } else {
      console.log("Final Answers:", ans);
      setLoading(true); 

      fetch("http://localhost:5000/analyze", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({ answers: Object.values(ans) }),
      })
        .then((res) => res.json())
        .then((data) => {
          setResult(data.result);
          setLoading(false);
        })
        .catch((error) => {
          console.error("Error:", error);
          setLoading(false);
        });
    }
  }

  return (
    <div className="container">
      {loading ? (
        <h2>Processing your responses... ‚è≥</h2>
      ) : result ? (
        <h2>Your love language is: {result} üíñ</h2>
      ) : (
        <>
          <h2>
            {index + 1}. {loveLanguageQuiz[index].question}
          </h2>
          <hr />
          <ul>
            {loveLanguageQuiz[index].options.map((option, i) => (
              <li
                key={i}
                onClick={() => handleSelection(option.type)}
                style={{
                  cursor: "pointer",
                  background: ans[index] === option.type ? "#ddd" : "white",
                }}
              >
                {option.text}
              </li>
            ))}
          </ul>
          <button onClick={handleClick} type="submit">
            {index < loveLanguageQuiz.length - 1 ? "Next" : "Submit"}
          </button>
          <p>
            {index + 1} of {loveLanguageQuiz.length} questions
          </p>
        </>
      )}
    </div>
  );
}

export default App;
