import React,{useState} from "react";

function chatbot(){
const [input,setInput] = useState("");
const [messages,setMessages] = useState([]);

async function sendMessage(){
    if(!input.trim()){
        return;
    }

setMessages([...messages,{sender:"You",text:input}]);
const res = await fetch("http://localhost:5000/chat", {
    method: "POST",
    headers: { "Content-Type": "application/json" },
   body:JSON.stringify({message:input}),
  });
  const data= await res.json();
  setMessages([...messages,{sender:"You", text:input}, {sender:"Bot", text:data.reply}]);
  setInput("");
}
return(<div style={{ border: "1px solid #ddd", padding: "10px", height: "300px", overflowY: "scroll" }}>
   { messages.map((msg,i)=> {
<p key={i}>{msg.sender} : {msg.text}</p>
    })}
    <input onChange={(e)=> setInput(e.target.value)} type="text" value={input} placeholder="Ask something..." />
    <button onClick={sendMessage}>Send</button>
</div>)
}
export default chatbot;
